import { useState } from "react";
import * as S from "./style";

export function Login() {
  const [form, setForm] = useState({
    email: {
      hasChanger: false,
      value: "",
    },
    password: {
      hasChanger: false,
      value: "",
    },
  });

  const isEmailValid = (email: string) => {
    return /\S+@\S+\.\S+/.test(email);
  };

  return (
    <S.Container>
      <section>
        <form>
          <input
            value={form.email.value}
            onChange={(event) =>
              setForm({
                ...form,
                email: {
                  hasChanger: true,
                  value: event.target.value,
                },
              })
            }
            type="email"
            placeholder="E-mail"
            data-testid="email"
          />
          {form.email.hasChanger && !form.email.value && (
            <div data-testid="email-required">Email é obrigatório</div>
          )}
          {form.email.hasChanger && !isEmailValid(form.email.value) && (
            <div data-testid="email-invalid">Email é inválido</div>
          )}
          <br />
          <br />
          <input
            onChange={(event) =>
              setForm({
                ...form,
                password: {
                  hasChanger: true,
                  value: event.target.value,
                },
              })
            }
            value={form.password.value}
            type="password"
            placeholder="Senha"
            data-testid="password"
          />
          {form.password.hasChanger && !form.password.value && (
            <div data-testid="password-required">Senha é obrigatória</div>
          )}
          <br />
          <br />
          <button type="button">Recuperar senha</button>
          <br />
          <button type="button">Entrar</button>
          <br />
          <button type="button">Registrar</button>
        </form>
      </section>
    </S.Container>
  );
}
