import styled from "styled-components";
import { colors, sizes } from "../../theme";

export const Container = styled.div`
  background-color: #01095c;
  opacity: 0.4;
  height: 100vh;
  width: 100vw;
  display: flex;
  justify-content: center;
  align-items: center;
  color: #ffffff;
  flex-direction: column;
  position: absolute;
  top: 0px;
  left: 0px;
  overflow: hidden;
  filter: blur(15px);
`;
